import math 

#Klartext
str_Klar = ''
#vorgegebene n
a = int(input('eine Nummer eingeben: '))

#String läuft von A bis Z min Länge 2
def Laenge_2():	
	#Entropie von Klartexten Fn 
	Fn = 0
	for i in range(65, 90):
		print('Buchstaben: ', chr(i))
		for j in range(65, 90):
			for k in range(65,90):
				#strings mit Buchstaben
				string_1 = str(chr(j)) + str(chr(k)) + str(chr(i))
				count_1 = str_Klar.count(string_1)
				#strings 
				string_2 = str(chr(j)) + str(chr(k))	
				count_2 = str_Klar.count(string_2)
				if count_1 > 0:		
					print(string_1)
					print('Menge des Strings mit Buchstaben in Klartext: ',count_1)
					#relative Häufigkeit 1
					Pr1 = count_1/(len(str_Klar) - 1)
					print('relative Häufigkeit 1 (%s): ' %string_1, Pr1)
					#relative Häufigkeit 2 
					Pr2 = count_1/count_2
					print('relative Häufigkeit 2 (%s): ' %string_1, Pr2)
					#Entropie von Klartexten Fn 
					Fn += - Pr1*math.log2(Pr2)
					print('Fn = ', Fn)

